mapper.select:
select instance.id, instance.process_definition_id, instance.command_type, instance.executor_id, instance.name, instance.state, instance.schedule_time, instance.start_time, instance.end_time, instance.run_times, instance.recovery, instance.host, instance.scheduler_interval, instance.scheduler_batch_no, instance.process_type, instance.dependent_scheduler_type, instance.scheduler_start_id, instance.scheduler_rerun_no, instance.rerun_scheduler_flag, instance.dependent_scheduler_flag from t_ds_process_instance instance join t_ds_process_definition define ON instance.process_definition_id = define.id where instance.is_sub_process=0
and instance.process_definition_id = #{processDefinitionId}
and instance.name like concat('%', #{searchVal}, '%')
and instance.start_time > #{startTime} and instance.start_time <= #{endTime}
and instance.state in
#{i}
and (instance.state != 98 and instance.state != 99)
and instance.host like concat('%', #{host}, '%')
and instance.executor_id = #{executorId}
order by instance.start_time desc
